"use client";
import React, { useState } from "react";
import { Card, Button, Row, Col, Input, message, Tag } from "antd";
import { ShoppingCartOutlined } from "@ant-design/icons";

const { Meta } = Card;
const { Search } = Input;

interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  discount: number; // in percent
  image: string;
}

const mockProducts: Product[] = [
  {
    id: 1,
    name: "ຊຸດຜູ້ຊາຍ",
    description: "ເສື້ອຜ້າແຟຊັ່ນ ສຳລັບຜູ້ຊາຍສະບາຍໆ",
    price: 550000,
    discount: 20,
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 2,
    name: "ເສື້ອຜູ້ຍິງ",
    description: "ເສື້ອກ້າມ ແຟຊັ່ນຜູ້ຍິງ ",
    price: 290000,
    discount: 10,
    image:
      "https://today-obs.line-scdn.net/0h4kkxGpPRa0RWAUZJ5lsUE2xXaCtlbXhHMjc6RwpvNXAoNnxBP2ZxKnUGYncpNSwaODAnJHADcHVyYStGaDBx/w644",
  },
  {
    id: 3,
    name: "ຊຸດຜູ້ຍິງ",
    description: "ໂສ້ງຂາສັ້ນ ເສື້ອ ສຳລັບແຟຊັ່ນວັງສະໄຫມ",
    price: 1900000,
    discount: 15,
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 4,
    name: "ເສື້ອ ສະເວັດເຕີ",
    description: "ເສື້ອແພເບົາບາງນຸ່ງສະບາຍ",
    price: 150000,
    discount: 20,
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 5,
    name: "dress",
    description: "ເສື້ອ ແຟຊັ່ນຜູ້ຍິງ ",
    price: 190000000,
    discount: 10,
    image:
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR5lfuyC010me67AY3XkyW457XC4qj75i_tFQ&s",
  },
  {
    id: 6,
    name: "ຊຸດຜູ້ຍິງ",
    description: "ໂສ້ງຂາສັ້ນ ເສື້ອ ສຳລັບແຟຊັ່ນວັງສະໄຫມ",
    price: 120000,
    discount: 15,
    image:
      "data:image/jpeg;base64,",
  },
];

const ProductManagement: React.FC = () => {
  const [cart, setCart] = useState<Product[]>([]);
  const [search, setSearch] = useState("");

  const handleAddToCart = (product: Product) => {
    setCart([...cart, product]);
    message.success(`${product.name} ເພີ່ມເຂົ້າຕະກ້າແລ້ວ`);
  };

  const filteredProducts = mockProducts.filter((p) =>
    p.name.toLowerCase().includes(search.toLowerCase())
  );

  const getDiscountedPrice = (price: number, discount: number) => {
    return price - price * (discount / 100);
  };

  return (
    <div style={{ padding: "30px" }}>
      <h1 style={{ marginBottom: "24px" }}>👗 ສິນຄ້າແຟຊັ່ນ</h1>

      <Search
        placeholder="ຄົ້ນຫາສິນຄ້າ..."
        allowClear
        enterButton="ຄົ້ນຫາ"
        size="large"
        onChange={(e) => setSearch(e.target.value)}
        style={{ marginBottom: 24, maxWidth: 400 }}
      />

      <Row gutter={[24, 24]}>
        {filteredProducts.map((product) => (
          <Col xs={24} sm={12} md={8} key={product.id}>
            <Card
              hoverable
              cover={
                <img
                  alt={product.name}
                  src={product.image}
                  style={{ height: 300, objectFit: "cover" }}
                />
              }
              actions={[
                <Button
                  type="primary"
                  icon={<ShoppingCartOutlined />}
                  onClick={() => handleAddToCart(product)}
                  block
                >
                  ເພີ່ມເຂົ້າຕະກ້າ
                </Button>,
              ]}
            >
              <Meta
                title={
                  <>
                    {product.name}{" "}
                    <Tag color="red">{product.discount}% ລົດ</Tag>
                  </>
                }
                description={
                  <>
                    <div>{product.description}</div>
                    <div style={{ marginTop: 8 }}>
                      <span
                        style={{
                          textDecoration: "line-through",
                          color: "#999",
                          marginRight: 8,
                        }}
                      >
                        {product.price.toLocaleString()} ກີບ
                      </span>
                      <span style={{ fontWeight: "bold", color: "#1677ff" }}>
                        {getDiscountedPrice(
                          product.price,
                          product.discount
                        ).toLocaleString()}{" "}
                        ກີບ
                      </span>
                    </div>
                  </>
                }
              />
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default ProductManagement;
